package com.example.myplugin;

import org.bukkit.entity.Player;
import org.bukkit.event.EventHandler;
import org.bukkit.event.Listener;
import org.bukkit.event.player.PlayerJoinEvent;
import org.bukkit.event.player.PlayerQuitEvent;
import org.bukkit.event.player.PlayerInteractEvent;
import org.bukkit.Material;
import org.bukkit.ChatColor;

public class PlayerListener implements Listener {

    // Oyuncu sunucuya girdiƒüinde
    @EventHandler
    public void onPlayerJoin(PlayerJoinEvent event) {
        Player player = event.getPlayer();

        // Mesaj deƒüi≈ütir
        event.setJoinMessage(ChatColor.GREEN + "[+] " + player.getName() + " sunucuya katƒ±ldƒ±!");

        // Oyuncuya mesaj g√∂nder
        player.sendMessage(ChatColor.GOLD + "Ho≈ü geldin " + player.getName() + "!");

        // Title g√∂ster
        player.sendTitle(
            ChatColor.YELLOW + "Ho≈ü Geldin!",
            ChatColor.GRAY + "ƒ∞yi eƒülenceler",
            10, 70, 20
        );
    }

    // Oyuncu sunucudan √ßƒ±ktƒ±ƒüƒ±nda
    @EventHandler
    public void onPlayerQuit(PlayerQuitEvent event) {
        Player player = event.getPlayer();
        event.setQuitMessage(ChatColor.RED + "[-] " + player.getName() + " ayrƒ±ldƒ±!");
    }

    // Oyuncu bir ≈üeye tƒ±kladƒ±ƒüƒ±nda
    @EventHandler
    public void onPlayerInteract(PlayerInteractEvent event) {
        Player player = event.getPlayer();

        // Elde tuttuƒüu item
        if (event.getItem() != null && event.getItem().getType() == Material.DIAMOND) {
            player.sendMessage(ChatColor.AQUA + "Elmas tutuyorsun!");

            // Particle efekti
            player.getWorld().spawnParticle(
                org.bukkit.Particle.VILLAGER_HAPPY,
                player.getLocation(),
                30
            );
        }
    }
}

