package com.example.myplugin;

import org.bukkit.command.Command;
import org.bukkit.command.CommandExecutor;
import org.bukkit.command.CommandSender;
import org.bukkit.command.TabCompleter;
import org.bukkit.entity.Player;
import org.bukkit.ChatColor;
import java.util.ArrayList;
import java.util.List;

public class MyCommand implements CommandExecutor, TabCompleter {

    @Override
    public boolean onCommand(CommandSender sender, Command cmd, String label, String[] args) {

        // Sadece oyuncular kullanabilir kontrol√º
        if (!(sender instanceof Player)) {
            sender.sendMessage(ChatColor.RED + "Bu komut sadece oyuncular i√ßin!");
            return true;
        }

        Player player = (Player) sender;

        // /heal komutu
        if (cmd.getName().equalsIgnoreCase("heal")) {

            // Permission kontrol√º
            if (!player.hasPermission("myplugin.heal")) {
                player.sendMessage(ChatColor.RED + "Bu komutu kullanma yetkin yok!");
                return true;
            }

            // Arg√ºman kontrol√º
            if (args.length == 0) {
                // Kendini iyile≈ütir
                player.setHealth(20.0);
                player.setFoodLevel(20);
                player.sendMessage(ChatColor.GREEN + "ƒ∞yile≈ütirildin!");
            } else {
                // Ba≈üka oyuncuyu iyile≈ütir
                Player target = player.getServer().getPlayer(args[0]);

                if (target == null) {
                    player.sendMessage(ChatColor.RED + "Oyuncu bulunamadƒ±!");
                    return true;
                }

                target.setHealth(20.0);
                target.setFoodLevel(20);
                player.sendMessage(ChatColor.GREEN + target.getName() + " iyile≈ütirildi!");
                target.sendMessage(ChatColor.GREEN + "ƒ∞yile≈ütirildin!");
            }

            return true;
        }

        // /give komutu - /give <oyuncu> <item> <miktar>
        if (cmd.getName().equalsIgnoreCase("give")) {

            if (args.length < 3) {
                player.sendMessage(ChatColor.RED + "Kullanƒ±m: /give <oyuncu> <item> <miktar>");
                return true;
            }

            Player target = player.getServer().getPlayer(args[0]);
            if (target == null) {
                player.sendMessage(ChatColor.RED + "Oyuncu bulunamadƒ±!");
                return true;
            }

            try {
                Material material = Material.valueOf(args[1].toUpperCase());
                int amount = Integer.parseInt(args[2]);

                ItemStack item = new ItemStack(material, amount);
                target.getInventory().addItem(item);

                player.sendMessage(ChatColor.GREEN + target.getName() + " adlƒ± oyuncuya " + amount + "x " + material.name() + " verildi!");
                target.sendMessage(ChatColor.GREEN + amount + "x " + material.name() + " aldƒ±n!");

            } catch (IllegalArgumentException e) {
                player.sendMessage(ChatColor.RED + "Ge√ßersiz item veya miktar!");
            }

            return true;
        }

        return false;
    }

    // Tab completion
    @Override
    public List<String> onTabComplete(CommandSender sender, Command cmd, String alias, String[] args) {
        List<String> list = new ArrayList<>();

        if (cmd.getName().equalsIgnoreCase("give")) {
            // ƒ∞lk arg√ºman - oyuncu isimleri
            if (args.length == 1) {
                for (Player p : sender.getServer().getOnlinePlayers()) {
                    list.add(p.getName());
                }
            }
            // ƒ∞kinci arg√ºman - item t√ºrleri
            else if (args.length == 2) {
                list.add("DIAMOND");
                list.add("IRON_INGOT");
                list.add("GOLD_INGOT");
                list.add("EMERALD");
            }
            // √ú√ß√ºnc√º arg√ºman - miktar
            else if (args.length == 3) {
                list.add("1");
                list.add("16");
                list.add("32");
                list.add("64");
            }
        }

        return list;
    }
}

