import org.bukkit.Material;
import org.bukkit.inventory.ItemStack;
import org.bukkit.inventory.meta.ItemMeta;
import org.bukkit.enchantments.Enchantment;
import org.bukkit.ChatColor;
import java.util.ArrayList;
import java.util.List;

public class ItemStackExample {

    public ItemStack createCustomItem() {

        // Basit item olu≈üturma
        ItemStack item = new ItemStack(Material.DIAMOND_SWORD, 1);

        // ItemMeta al
        ItemMeta meta = item.getItemMeta();

        // ƒ∞sim ayarla
        meta.setDisplayName(ChatColor.GOLD + "" + ChatColor.BOLD + "Efsanevi Kƒ±lƒ±√ß");

        // Lore (a√ßƒ±klama) ekle
        List<String> lore = new ArrayList<>();
        lore.add(ChatColor.GRAY + "Bu kƒ±lƒ±√ß efsanevi g√º√ßlere sahip!");
        lore.add(ChatColor.RED + "Hasar: +15");
        lore.add(ChatColor.BLUE + "Dayanƒ±klƒ±lƒ±k: Sonsuz");
        lore.add("");
        lore.add(ChatColor.YELLOW + "Efsanevi Kalite");
        meta.setLore(lore);

        // Unbreakable yap
        meta.setUnbreakable(true);

        // Meta'yƒ± item'a uygula
        item.setItemMeta(meta);

        // Enchantment ekle
        item.addUnsafeEnchantment(Enchantment.DAMAGE_ALL, 10); // Sharpness 10
        item.addUnsafeEnchantment(Enchantment.FIRE_ASPECT, 3);  // Fire Aspect 3
        item.addUnsafeEnchantment(Enchantment.KNOCKBACK, 5);    // Knockback 5

        return item;
    }

    public ItemStack createGlowingItem() {
        ItemStack item = new ItemStack(Material.DIAMOND, 1);
        ItemMeta meta = item.getItemMeta();

        meta.setDisplayName(ChatColor.AQUA + "Parlayan Elmas");

        // Glow efekti i√ßin invisible enchantment
        item.setItemMeta(meta);
        item.addUnsafeEnchantment(Enchantment.LUCK, 1);

        // Enchantment yazƒ±sƒ±nƒ± gizle
        meta = item.getItemMeta();
        meta.addItemFlags(org.bukkit.inventory.ItemFlag.HIDE_ENCHANTS);
        item.setItemMeta(meta);

        return item;
    }

    public ItemStack createPlayerHead(String playerName) {
        ItemStack skull = new ItemStack(Material.PLAYER_HEAD, 1);
        org.bukkit.inventory.meta.SkullMeta meta = (org.bukkit.inventory.meta.SkullMeta) skull.getItemMeta();

        meta.setOwner(playerName);
        meta.setDisplayName(ChatColor.YELLOW + playerName + "'in Kafasƒ±");

        skull.setItemMeta(meta);
        return skull;
    }

    public ItemStack createPotion() {
        ItemStack potion = new ItemStack(Material.POTION, 1);
        org.bukkit.inventory.meta.PotionMeta meta = (org.bukkit.inventory.meta.PotionMeta) potion.getItemMeta();

        // Potion efekti ekle
        meta.addCustomEffect(
            new org.bukkit.potion.PotionEffect(
                org.bukkit.potion.PotionEffectType.SPEED,
                200,  // S√ºre (tick)
                2     // Seviye (0 = Level 1)
            ),
            true
        );

        meta.setDisplayName(ChatColor.LIGHT_PURPLE + "S√ºper Hƒ±z ƒ∞ksiri");
        meta.setColor(org.bukkit.Color.PURPLE);

        potion.setItemMeta(meta);
        return potion;
    }

    // Item kar≈üƒ±la≈ütƒ±rma
    public boolean compareItems(ItemStack item1, ItemStack item2) {
        if (item1 == null || item2 == null) return false;

        // Sadece tip kontrol√º
        if (item1.getType() == item2.getType()) {
            return true;
        }

        // Tam e≈üitlik (meta dahil)
        return item1.isSimilar(item2);
    }
}

import org.bukkit.entity.Player;
import org.bukkit.event.Event;
import org.bukkit.event.HandlerList;
import org.bukkit.event.Cancellable;

// Custom Event class
public class PlayerLevelUpEvent extends Event implements Cancellable {

    private static final HandlerList HANDLERS = new HandlerList();
    private boolean cancelled = false;

    private final Player player;
    private final int oldLevel;
    private final int newLevel;

    public PlayerLevelUpEvent(Player player, int oldLevel, int newLevel) {
        this.player = player;
        this.oldLevel = oldLevel;
        this.newLevel = newLevel;
    }

    public Player getPlayer() {
        return player;
    }

    public int getOldLevel() {
        return oldLevel;
    }

    public int getNewLevel() {
        return newLevel;
    }

    @Override
    public boolean isCancelled() {
        return cancelled;
    }

    @Override
    public void setCancelled(boolean cancel) {
        this.cancelled = cancel;
    }

    @Override
    public HandlerList getHandlers() {
        return HANDLERS;
    }

    public static HandlerList getHandlerList() {
        return HANDLERS;
    }
}

// Event'i tetikleme
public class LevelManager {

    public void giveLevel(Player player, int amount) {
        int oldLevel = player.getLevel();
        int newLevel = oldLevel + amount;

        // Custom event olu≈ütur
        PlayerLevelUpEvent event = new PlayerLevelUpEvent(player, oldLevel, newLevel);

        // Event'i √ßaƒüƒ±r
        Bukkit.getPluginManager().callEvent(event);

        // Eƒüer cancel edilmediyse
        if (!event.isCancelled()) {
            player.setLevel(newLevel);
            player.sendMessage(ChatColor.GREEN + "Level atladƒ±n! Yeni level: " + newLevel);
        }
    }
}

// Event'i dinleme
public class CustomEventListener implements Listener {

    @EventHandler
    public void onPlayerLevelUp(PlayerLevelUpEvent event) {
        Player player = event.getPlayer();
        int newLevel = event.getNewLevel();

        // Her 5 levelde bir √∂d√ºl ver
        if (newLevel % 5 == 0) {
            ItemStack reward = new ItemStack(Material.DIAMOND, 5);
            player.getInventory().addItem(reward);
            player.sendMessage(ChatColor.GOLD + "Level √∂d√ºl√º aldƒ±n: 5x Elmas!");
        }

        // Level 10'da event'i cancel et (√∂rnek)
        if (newLevel > 10) {
            event.setCancelled(true);
            player.sendMessage(ChatColor.RED + "Maksimum level 10!");
        }
    }
}

import org.bukkit.scheduler.BukkitRunnable;
import org.bukkit.scheduler.BukkitTask;
import org.bukkit.plugin.Plugin;
import org.bukkit.Bukkit;
import org.bukkit.entity.Player;
import org.bukkit.ChatColor;

public class SchedulerExamples {

    private Plugin plugin;

    public SchedulerExamples(Plugin plugin) {
        this.plugin = plugin;
    }

    // Basit gecikme (1 saniye = 20 tick)
    public void delayedTask() {
        Bukkit.getScheduler().runTaskLater(plugin, new Runnable() {
            @Override
            public void run() {
                Bukkit.broadcastMessage(ChatColor.YELLOW + "3 saniye sonra √ßalƒ±≈ütƒ±m!");
            }
        }, 60L); // 60 tick = 3 saniye
    }

    // Tekrarlayan g√∂rev
    public void repeatingTask() {
        Bukkit.getScheduler().runTaskTimer(plugin, new Runnable() {
            @Override
            public void run() {
                Bukkit.broadcastMessage(ChatColor.GREEN + "Her 5 saniyede bir mesaj!");
            }
        }, 0L, 100L); // 0 tick gecikme, her 100 tick'te bir (5 saniye)
    }

    // BukkitRunnable ile (daha geli≈ümi≈ü)
    public void countdown(Player player) {
        new BukkitRunnable() {
            int count = 5;

            @Override
            public void run() {
                if (count <= 0) {
                    player.sendMessage(ChatColor.GREEN + "Ba≈üla!");
                    player.playSound(player.getLocation(), Sound.ENTITY_PLAYER_LEVELUP, 1.0f, 1.0f);
                    cancel(); // Task'ƒ± durdur
                    return;
                }

                player.sendTitle(
                    ChatColor.YELLOW + String.valueOf(count),
                    "",
                    0, 20, 10
                );
                player.playSound(player.getLocation(), Sound.BLOCK_NOTE_BLOCK_HAT, 1.0f, 1.0f);

                count--;
            }
        }.runTaskTimer(plugin, 0L, 20L); // Her 1 saniyede bir
    }

    // Async task (aƒüƒ±r i≈ülemler i√ßin)
    public void asyncTask() {
        Bukkit.getScheduler().runTaskAsynchronously(plugin, new Runnable() {
            @Override
            public void run() {
                // Aƒüƒ±r hesaplama veya database i≈ülemi
                // Dƒ∞KKAT: Bukkit API'si burada kullanƒ±lmamalƒ±!

                String result = heavyCalculation();

                // Sonucu ana thread'de kullan
                Bukkit.getScheduler().runTask(plugin, new Runnable() {
                    @Override
                    public void run() {
                        Bukkit.broadcastMessage("Sonu√ß: " + result);
                    }
                });
            }
        });
    }

    // Task'ƒ± iptal etme
    public void cancellableTask() {
        BukkitTask task = Bukkit.getScheduler().runTaskTimer(plugin, new Runnable() {
            int count = 0;

            @Override
            public void run() {
                count++;
                Bukkit.broadcastMessage("√áalƒ±≈üma sayƒ±sƒ±: " + count);
            }
        }, 0L, 20L);

        // 10 saniye sonra task'ƒ± iptal et
        Bukkit.getScheduler().runTaskLater(plugin, new Runnable() {
            @Override
            public void run() {
                task.cancel();
                Bukkit.broadcastMessage(ChatColor.RED + "Task iptal edildi!");
            }
        }, 200L);
    }

    // Oyuncu i√ßin ki≈üisel zamanlayƒ±cƒ±
    public void playerCooldown(Player player) {
        player.sendMessage(ChatColor.YELLOW + "Bekleme s√ºresi ba≈üladƒ±!");

        new BukkitRunnable() {
            int timeLeft = 30; // 30 saniye

            @Override
            public void run() {
                if (timeLeft <= 0) {
                    player.sendMessage(ChatColor.GREEN + "Bekleme s√ºresi bitti!");
                    player.playSound(player.getLocation(), Sound.ENTITY_EXPERIENCE_ORB_PICKUP, 1.0f, 1.0f);
                    cancel();
                    return;
                }

                if (timeLeft % 10 == 0 || timeLeft <= 5) {
                    player.sendActionBar(ChatColor.YELLOW + "Kalan s√ºre: " + timeLeft + " saniye");
                }

                timeLeft--;
            }
        }.runTaskTimer(plugin, 0L, 20L);
    }

    private String heavyCalculation() {
        // √ñrnek aƒüƒ±r i≈ülem
        return "ƒ∞≈ülem tamamlandƒ±";
    }
}

import org.bukkit.configuration.file.FileConfiguration;
import org.bukkit.configuration.file.YamlConfiguration;
import java.io.File;
import java.io.IOException;
import java.util.List;

public class ConfigExample {

    private Plugin plugin;
    private FileConfiguration config;

    public ConfigExample(Plugin plugin) {
        this.plugin = plugin;
        this.config = plugin.getConfig();
    }

    // Config'i y√ºkle ve default deƒüerleri ayarla
    public void setupConfig() {
        // Default config.yml olu≈ütur (resources klas√∂r√ºnden)
        plugin.saveDefaultConfig();

        // Config'i yeniden y√ºkle
        plugin.reloadConfig();
        config = plugin.getConfig();
    }

    // Deƒüer okuma √∂rnekleri
    public void readValues() {
        // String
        String serverName = config.getString("server.name", "Default Server");

        // Integer
        int maxPlayers = config.getInt("server.max-players", 100);

        // Boolean
        boolean pvpEnabled = config.getBoolean("gameplay.pvp-enabled", true);

        // Double
        double xpMultiplier = config.getDouble("gameplay.xp-multiplier", 1.0);

        // List
        List<String> blockedCommands = config.getStringList("blocked-commands");

        // ƒ∞√ß i√ße deƒüer
        String welcomeMessage = config.getString("messages.welcome", "Ho≈ü geldin!");

        // Kullanƒ±m
        plugin.getLogger().info("Server: " + serverName);
        plugin.getLogger().info("Max Players: " + maxPlayers);
        plugin.getLogger().info("PvP: " + pvpEnabled);
    }

    // Deƒüer yazma
    public void writeValues() {
        config.set("server.name", "My Awesome Server");
        config.set("server.max-players", 50);
        config.set("gameplay.pvp-enabled", false);

        // Liste
        List<String> commands = Arrays.asList("/stop", "/op", "/deop");
        config.set("blocked-commands", commands);

        // Config'i kaydet
        try {
            config.save(new File(plugin.getDataFolder(), "config.yml"));
        } catch (IOException e) {
            plugin.getLogger().severe("Config kaydedilemedi: " + e.getMessage());
        }
    }

    // √ñzel config dosyasƒ±
    private FileConfiguration playerData;
    private File playerDataFile;

    public void createCustomConfig() {
        playerDataFile = new File(plugin.getDataFolder(), "playerdata.yml");

        if (!playerDataFile.exists()) {
            playerDataFile.getParentFile().mkdirs();
            plugin.saveResource("playerdata.yml", false);
        }

        playerData = YamlConfiguration.loadConfiguration(playerDataFile);
    }

    public void savePlayerData(Player player) {
        String uuid = player.getUniqueId().toString();

        playerData.set("players." + uuid + ".name", player.getName());
        playerData.set("players." + uuid + ".level", player.getLevel());
        playerData.set("players." + uuid + ".last-login", System.currentTimeMillis());

        try {
            playerData.save(playerDataFile);
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public void loadPlayerData(Player player) {
        String uuid = player.getUniqueId().toString();

        if (playerData.contains("players." + uuid)) {
            int savedLevel = playerData.getInt("players." + uuid + ".level");
            long lastLogin = playerData.getLong("players." + uuid + ".last-login");

            player.setLevel(savedLevel);
            player.sendMessage("Son giri≈ü: " + new Date(lastLogin));
        }
    }
}

