import org.bukkit.Bukkit;
import org.bukkit.Material;
import org.bukkit.entity.Player;
import org.bukkit.event.EventHandler;
import org.bukkit.event.Listener;
import org.bukkit.event.inventory.InventoryClickEvent;
import org.bukkit.inventory.Inventory;
import org.bukkit.inventory.ItemStack;
import org.bukkit.inventory.meta.ItemMeta;
import org.bukkit.ChatColor;
import java.util.Arrays;

public class InventoryGUI implements Listener {

    // Basit GUI olu≈üturma
    public void openMainMenu(Player player) {
        // 3 satƒ±rlƒ±k (27 slot) inventory olu≈ütur
        Inventory inv = Bukkit.createInventory(null, 27, ChatColor.DARK_GRAY + "Ana Men√º");

        // ƒ∞temler olu≈ütur
        ItemStack teleport = createMenuItem(
            Material.ENDER_PEARL,
            ChatColor.LIGHT_PURPLE + "Teleport",
            ChatColor.GRAY + "Tƒ±kla ve teleport ol!"
        );

        ItemStack shop = createMenuItem(
            Material.EMERALD,
            ChatColor.GREEN + "Maƒüaza",
            ChatColor.GRAY + "E≈üya satƒ±n al"
        );

        ItemStack info = createMenuItem(
            Material.BOOK,
            ChatColor.YELLOW + "Bilgi",
            ChatColor.GRAY + "Sunucu bilgileri"
        );

        ItemStack close = createMenuItem(
            Material.BARRIER,
            ChatColor.RED + "Kapat",
            ChatColor.GRAY + "Men√ºy√º kapat"
        );

        // ƒ∞temleri inventory'ye yerle≈ütir
        inv.setItem(11, teleport);
        inv.setItem(13, shop);
        inv.setItem(15, info);
        inv.setItem(22, close);

        // Border ekle (dekorasyon)
        ItemStack border = createMenuItem(Material.GRAY_STAINED_GLASS_PANE, " ");
        for (int i = 0; i < 27; i++) {
            if (inv.getItem(i) == null) {
                inv.setItem(i, border);
            }
        }

        player.openInventory(inv);
    }

    // Geli≈ümi≈ü GUI - Sayfalƒ± sistem
    public void openPagedGUI(Player player, int page) {
        Inventory inv = Bukkit.createInventory(null, 54, ChatColor.DARK_PURPLE + "Oyuncular - Sayfa " + page);

        List<Player> players = new ArrayList<>(Bukkit.getOnlinePlayers());
        int startIndex = (page - 1) * 45;
        int endIndex = Math.min(startIndex + 45, players.size());

        // Oyuncularƒ± g√∂ster
        for (int i = startIndex; i < endIndex; i++) {
            Player p = players.get(i);
            ItemStack skull = new ItemStack(Material.PLAYER_HEAD);
            SkullMeta meta = (SkullMeta) skull.getItemMeta();
            meta.setOwner(p.getName());
            meta.setDisplayName(ChatColor.YELLOW + p.getName());
            meta.setLore(Arrays.asList(
                ChatColor.GRAY + "Level: " + p.getLevel(),
                ChatColor.GRAY + "Saƒülƒ±k: " + (int)p.getHealth() + "/20",
                "",
                ChatColor.GREEN + "Tƒ±kla ve teleport ol!"
            ));
            skull.setItemMeta(meta);
            inv.setItem(i - startIndex, skull);
        }

        // Navigasyon butonlarƒ±
        if (page > 1) {
            ItemStack prev = createMenuItem(
                Material.ARROW,
                ChatColor.YELLOW + "√ñnceki Sayfa",
                ChatColor.GRAY + "Sayfa " + (page - 1)
            );
            inv.setItem(48, prev);
        }

        if (endIndex < players.size()) {
            ItemStack next = createMenuItem(
                Material.ARROW,
                ChatColor.YELLOW + "Sonraki Sayfa",
                ChatColor.GRAY + "Sayfa " + (page + 1)
            );
            inv.setItem(50, next);
        }

        ItemStack close = createMenuItem(Material.BARRIER, ChatColor.RED + "Kapat");
        inv.setItem(49, close);

        player.openInventory(inv);
    }

    // Click event handler
    @EventHandler
    public void onInventoryClick(InventoryClickEvent event) {
        String title = event.getView().getTitle();

        // Sadece √∂zel GUI'miz ise
        if (!title.contains("Ana Men√º") && !title.contains("Oyuncular")) {
            return;
        }

        event.setCancelled(true); // Item alƒ±nmasƒ±nƒ± engelle

        Player player = (Player) event.getWhoClicked();
        ItemStack clicked = event.getCurrentItem();

        if (clicked == null || clicked.getType() == Material.AIR) {
            return;
        }

        // Ana Men√º tƒ±klamalarƒ±
        if (title.contains("Ana Men√º")) {
            handleMainMenuClick(player, clicked);
        }
        // Oyuncular men√ºs√º tƒ±klamalarƒ±
        else if (title.contains("Oyuncular")) {
            handlePlayersMenuClick(player, clicked, title);
        }
    }

    private void handleMainMenuClick(Player player, ItemStack clicked) {
        if (!clicked.hasItemMeta()) return;

        String displayName = clicked.getItemMeta().getDisplayName();

        if (displayName.contains("Teleport")) {
            player.closeInventory();
            player.sendMessage(ChatColor.GREEN + "Teleport men√ºs√º a√ßƒ±lƒ±yor...");
            // Teleport GUI'sini a√ß
        }
        else if (displayName.contains("Maƒüaza")) {
            player.closeInventory();
            player.sendMessage(ChatColor.GREEN + "Maƒüaza a√ßƒ±lƒ±yor...");
            // Shop GUI'sini a√ß
        }
        else if (displayName.contains("Bilgi")) {
            player.sendMessage(ChatColor.GOLD + "=== Sunucu Bilgileri ===");
            player.sendMessage(ChatColor.YELLOW + "Online: " + Bukkit.getOnlinePlayers().size());
            player.sendMessage(ChatColor.YELLOW + "TPS: 20.0");
        }
        else if (displayName.contains("Kapat")) {
            player.closeInventory();
        }
    }

    private void handlePlayersMenuClick(Player player, ItemStack clicked, String title) {
        String displayName = clicked.getItemMeta().getDisplayName();

        if (displayName.contains("√ñnceki Sayfa")) {
            int currentPage = Integer.parseInt(title.split("Sayfa ")[1]);
            openPagedGUI(player, currentPage - 1);
        }
        else if (displayName.contains("Sonraki Sayfa")) {
            int currentPage = Integer.parseInt(title.split("Sayfa ")[1]);
            openPagedGUI(player, currentPage + 1);
        }
        else if (displayName.contains("Kapat")) {
            player.closeInventory();
        }
        else if (clicked.getType() == Material.PLAYER_HEAD) {
            String targetName = ChatColor.stripColor(displayName);
            Player target = Bukkit.getPlayer(targetName);
            if (target != null) {
                player.teleport(target);
                player.sendMessage(ChatColor.GREEN + target.getName() + " adlƒ± oyuncuya ƒ±≈üƒ±nlandƒ±n!");
                player.closeInventory();
            }
        }
    }

    // Yardƒ±mcƒ± method - MenuItem olu≈üturma
    private ItemStack createMenuItem(Material material, String name, String... lore) {
        ItemStack item = new ItemStack(material);
        ItemMeta meta = item.getItemMeta();
        meta.setDisplayName(name);
        if (lore.length > 0) {
            meta.setLore(Arrays.asList(lore));
        }
        item.setItemMeta(meta);
        return item;
    }
}

import org.bukkit.Bukkit;
import org.bukkit.ChatColor;
import org.bukkit.entity.Player;
import org.bukkit.scoreboard.*;

public class ScoreboardManager {

    // Basit scoreboard olu≈üturma
    public void createScoreboard(Player player) {
        // Scoreboard manager
        ScoreboardManager manager = Bukkit.getScoreboardManager();
        Scoreboard board = manager.getNewScoreboard();

        // Objective olu≈ütur (sidebar)
        Objective objective = board.registerNewObjective(
            "main",
            "dummy",
            ChatColor.GOLD + "" + ChatColor.BOLD + "MY SERVER"
        );
        objective.setDisplaySlot(DisplaySlot.SIDEBAR);

        // Skorlarƒ± ekle (15'ten 0'a doƒüru sƒ±ralƒ±)
        Score space1 = objective.getScore(ChatColor.GRAY + "          ");
        space1.setScore(10);

        Score playerName = objective.getScore(ChatColor.WHITE + "Oyuncu: " + ChatColor.YELLOW + player.getName());
        playerName.setScore(9);

        Score level = objective.getScore(ChatColor.WHITE + "Level: " + ChatColor.GREEN + player.getLevel());
        level.setScore(8);

        Score space2 = objective.getScore(ChatColor.GRAY + "         ");
        space2.setScore(7);

        Score online = objective.getScore(ChatColor.WHITE + "Online: " + ChatColor.AQUA + Bukkit.getOnlinePlayers().size());
        online.setScore(6);

        Score space3 = objective.getScore(ChatColor.GRAY + "        ");
        space3.setScore(5);

        Score website = objective.getScore(ChatColor.GOLD + "www.server.com");
        website.setScore(4);

        // Scoreboard'u oyuncuya ver
        player.setScoreboard(board);
    }

    // Dinamik scoreboard (g√ºncellenen)
    public void updateScoreboard(Player player) {
        Scoreboard board = player.getScoreboard();

        if (board == null || board.getObjective("main") == null) {
            createScoreboard(player);
            return;
        }

        Objective objective = board.getObjective("main");

        // Eski skorlarƒ± temizle
        for (String entry : board.getEntries()) {
            board.resetScores(entry);
        }

        // Yeni skorlarƒ± ekle
        objective.getScore(ChatColor.GRAY + "          ").setScore(10);
        objective.getScore(ChatColor.WHITE + "Oyuncu: " + ChatColor.YELLOW + player.getName()).setScore(9);
        objective.getScore(ChatColor.WHITE + "Level: " + ChatColor.GREEN + player.getLevel()).setScore(8);
        objective.getScore(ChatColor.WHITE + "Can: " + ChatColor.RED + (int)player.getHealth() + "/20").setScore(7);
        objective.getScore(ChatColor.GRAY + "         ").setScore(6);
        objective.getScore(ChatColor.WHITE + "Online: " + ChatColor.AQUA + Bukkit.getOnlinePlayers().size()).setScore(5);
        objective.getScore(ChatColor.GRAY + "        ").setScore(4);
        objective.getScore(ChatColor.GOLD + "www.server.com").setScore(3);
    }

    // Otomatik g√ºncelleme (her 1 saniyede)
    public void startAutoUpdate(Plugin plugin, Player player) {
        new BukkitRunnable() {
            @Override
            public void run() {
                if (!player.isOnline()) {
                    cancel();
                    return;
                }
                updateScoreboard(player);
            }
        }.runTaskTimer(plugin, 0L, 20L);
    }

    // Team olu≈üturma (isim rengi, prefix, suffix)
    public void createTeams(Scoreboard board) {
        // Admin takƒ±mƒ±
        Team admin = board.registerNewTeam("admin");
        admin.setPrefix(ChatColor.RED + "[ADMIN] ");
        admin.setColor(ChatColor.RED);
        admin.setCanSeeFriendlyInvisibles(true);

        // VIP takƒ±mƒ±
        Team vip = board.registerNewTeam("vip");
        vip.setPrefix(ChatColor.GOLD + "[VIP] ");
        vip.setColor(ChatColor.GOLD);

        // Normal takƒ±m
        Team normal = board.registerNewTeam("normal");
        normal.setColor(ChatColor.GRAY);
    }

    // Oyuncuyu takƒ±ma ekleme
    public void addPlayerToTeam(Player player, String teamName) {
        Scoreboard board = player.getScoreboard();
        Team team = board.getTeam(teamName);

        if (team != null) {
            team.addEntry(player.getName());
        }
    }

    // PvP takƒ±mlarƒ± (kƒ±rmƒ±zƒ± vs mavi)
    public void createPvPTeams(Scoreboard board) {
        // Kƒ±rmƒ±zƒ± takƒ±m
        Team red = board.registerNewTeam("red");
        red.setColor(ChatColor.RED);
        red.setPrefix(ChatColor.RED + "[RED] ");
        red.setAllowFriendlyFire(false); // Takƒ±m arkada≈üƒ±na vuramaz
        red.setOption(Team.Option.NAME_TAG_VISIBILITY, Team.OptionStatus.FOR_OWN_TEAM);

        // Mavi takƒ±m
        Team blue = board.registerNewTeam("blue");
        blue.setColor(ChatColor.BLUE);
        blue.setPrefix(ChatColor.BLUE + "[BLUE] ");
        blue.setAllowFriendlyFire(false);
        blue.setOption(Team.Option.NAME_TAG_VISIBILITY, Team.OptionStatus.FOR_OWN_TEAM);
    }

    // Scoreboard temizleme
    public void clearScoreboard(Player player) {
        player.setScoreboard(Bukkit.getScoreboardManager().getNewScoreboard());
    }
}

import org.bukkit.Location;
import org.bukkit.Particle;
import org.bukkit.Sound;
import org.bukkit.World;
import org.bukkit.entity.Player;
import org.bukkit.scheduler.BukkitRunnable;
import org.bukkit.util.Vector;

public class EffectsUtil {

    // Basit particle spawn
    public void spawnParticle(Location loc, Particle particle, int count) {
        World world = loc.getWorld();
        world.spawnParticle(particle, loc, count);
    }

    // Pop√ºler particle efektleri
    public void commonParticles(Player player) {
        Location loc = player.getLocation().add(0, 1, 0);

        // Kalpler
        player.getWorld().spawnParticle(Particle.HEART, loc, 10);

        // Alev
        player.getWorld().spawnParticle(Particle.FLAME, loc, 20);

        // B√ºy√º masasƒ±
        player.getWorld().spawnParticle(Particle.ENCHANTMENT_TABLE, loc, 30);

        // K√∂y sakini mutlu
        player.getWorld().spawnParticle(Particle.VILLAGER_HAPPY, loc, 15);

        // K√∂y sakini kƒ±zgƒ±n
        player.getWorld().spawnParticle(Particle.VILLAGER_ANGRY, loc, 15);

        // Portal
        player.getWorld().spawnParticle(Particle.PORTAL, loc, 50);

        // End rod
        player.getWorld().spawnParticle(Particle.END_ROD, loc, 20);

        // Redstone (renkli)
        player.getWorld().spawnParticle(
            Particle.REDSTONE,
            loc,
            10,
            new Particle.DustOptions(org.bukkit.Color.BLUE, 1.0f)
        );
    }

    // Daire ≈üeklinde particle
    public void circleParticle(Location center, Particle particle, double radius) {
        for (double i = 0; i < 2 * Math.PI; i += Math.PI / 16) {
            double x = center.getX() + (radius * Math.cos(i));
            double z = center.getZ() + (radius * Math.sin(i));
            Location loc = new Location(center.getWorld(), x, center.getY(), z);
            center.getWorld().spawnParticle(particle, loc, 1);
        }
    }

    // Helix (spiral) efekti
    public void helixEffect(Plugin plugin, Location start, double height) {
        new BukkitRunnable() {
            double y = 0;
            double angle = 0;

            @Override
            public void run() {
                if (y >= height) {
                    cancel();
                    return;
                }

                double x = start.getX() + Math.cos(angle);
                double z = start.getZ() + Math.sin(angle);
                Location loc = new Location(start.getWorld(), x, start.getY() + y, z);

                start.getWorld().spawnParticle(Particle.FLAME, loc, 1);

                y += 0.1;
                angle += Math.PI / 8;
            }
        }.runTaskTimer(plugin, 0L, 1L);
    }

    // Sound efektleri
    public void playSounds(Player player) {
        Location loc = player.getLocation();

        // Level up
        player.playSound(loc, Sound.ENTITY_PLAYER_LEVELUP, 1.0f, 1.0f);

        // XP orb
        player.playSound(loc, Sound.ENTITY_EXPERIENCE_ORB_PICKUP, 1.0f, 1.0f);

        // Click
        player.playSound(loc, Sound.UI_BUTTON_CLICK, 1.0f, 1.0f);

        // Ba≈üarƒ±
        player.playSound(loc, Sound.ENTITY_PLAYER_LEVELUP, 1.0f, 2.0f);

        // Hata
        player.playSound(loc, Sound.ENTITY_VILLAGER_NO, 1.0f, 1.0f);

        // Patlama
        player.playSound(loc, Sound.ENTITY_GENERIC_EXPLODE, 1.0f, 1.0f);

        // Enderman teleport
        player.playSound(loc, Sound.ENTITY_ENDERMAN_TELEPORT, 1.0f, 1.0f);

        // Pling (not bloƒüu)
        player.playSound(loc, Sound.BLOCK_NOTE_BLOCK_PLING, 1.0f, 2.0f);
    }

    // √ñzel ses pitch'i
    public void playCustomSound(Player player, Sound sound, float volume, float pitch) {
        // pitch: 0.5 = d√º≈ü√ºk ton, 2.0 = y√ºksek ton
        player.playSound(player.getLocation(), sound, volume, pitch);
    }

    // Particle trail (iz) olu≈üturma
    public void createTrail(Plugin plugin, Player player, Particle particle) {
        new BukkitRunnable() {
            @Override
            public void run() {
                if (!player.isOnline()) {
                    cancel();
                    return;
                }

                Location loc = player.getLocation().add(0, 0.1, 0);
                player.getWorld().spawnParticle(particle, loc, 2);
            }
        }.runTaskTimer(plugin, 0L, 2L);
    }

    // Firework effect
    public void spawnFirework(Location loc) {
        org.bukkit.entity.Firework fw = loc.getWorld().spawn(loc, org.bukkit.entity.Firework.class);
        org.bukkit.inventory.meta.FireworkMeta meta = fw.getFireworkMeta();

        org.bukkit.FireworkEffect effect = org.bukkit.FireworkEffect.builder()
            .with(org.bukkit.FireworkEffect.Type.BALL_LARGE)
            .withColor(org.bukkit.Color.RED)
            .withFade(org.bukkit.Color.YELLOW)
            .flicker(true)
            .trail(true)
            .build();

        meta.addEffect(effect);
        meta.setPower(1);
        fw.setFireworkMeta(meta);
    }
}

import org.bukkit.Bukkit;
import org.bukkit.boss.BarColor;
import org.bukkit.boss.BarStyle;
import org.bukkit.boss.BossBar;
import org.bukkit.entity.Player;
import org.bukkit.ChatColor;
import org.bukkit.scheduler.BukkitRunnable;

public class DisplayUtils {

    // ActionBar g√∂nderme
    public void sendActionBar(Player player, String message) {
        player.sendActionBar(message);
    }

    // Saya√ßlƒ± ActionBar
    public void actionBarCountdown(Plugin plugin, Player player, int seconds) {
        new BukkitRunnable() {
            int timeLeft = seconds;

            @Override
            public void run() {
                if (timeLeft <= 0) {
                    player.sendActionBar(ChatColor.GREEN + "Hazƒ±r!");
                    cancel();
                    return;
                }

                player.sendActionBar(ChatColor.YELLOW + "Kalan s√ºre: " + timeLeft + " saniye");
                timeLeft--;
            }
        }.runTaskTimer(plugin, 0L, 20L);
    }

    // Progress bar ActionBar
    public void actionBarProgress(Player player, int current, int max) {
        double percentage = (double) current / max;
        int bars = (int) (percentage * 20);

        StringBuilder progress = new StringBuilder(ChatColor.GREEN + "[");
        for (int i = 0; i < 20; i++) {
            if (i < bars) {
                progress.append("|");
            } else {
                progress.append(ChatColor.GRAY).append("|");
            }
        }
        progress.append(ChatColor.GREEN).append("] ");
        progress.append(ChatColor.YELLOW).append((int)(percentage * 100)).append("%");

        player.sendActionBar(progress.toString());
    }

    // BossBar olu≈üturma
    public BossBar createBossBar(String title, BarColor color, BarStyle style) {
        return Bukkit.createBossBar(title, color, style);
    }

    // Basit BossBar √∂rneƒüi
    public void showBossBar(Player player, String title) {
        BossBar bar = Bukkit.createBossBar(
            ChatColor.GOLD + title,
            BarColor.YELLOW,
            BarStyle.SOLID
        );

        bar.addPlayer(player);
        bar.setProgress(1.0); // 0.0 - 1.0 arasƒ±
        bar.setVisible(true);

        // 5 saniye sonra kaldƒ±r
        Bukkit.getScheduler().runTaskLater(plugin, () -> {
            bar.removePlayer(player);
        }, 100L);
    }

    // Zamanlayƒ±cƒ± BossBar
    public void timerBossBar(Plugin plugin, Player player, int seconds) {
        BossBar bar = Bukkit.createBossBar(
            ChatColor.RED + "S√ºre Dolmak √úzere!",
            BarColor.RED,
            BarStyle.SEGMENTED_10
        );

        bar.addPlayer(player);
        bar.setVisible(true);

        new BukkitRunnable() {
            int timeLeft = seconds;

            @Override
            public void run() {
                if (timeLeft <= 0) {
                    bar.setTitle(ChatColor.GREEN + "S√ºre Doldu!");
                    bar.setColor(BarColor.GREEN);
                    Bukkit.getScheduler().runTaskLater(plugin, () -> {
                        bar.removePlayer(player);
                    }, 40L);
                    cancel();
                    return;
                }

                double progress = (double) timeLeft / seconds;
                bar.setProgress(progress);
                bar.setTitle(ChatColor.YELLOW + "Kalan: " + timeLeft + " saniye");

                // Renk deƒüi≈üimi
                if (timeLeft <= 10) {
                    bar.setColor(BarColor.RED);
                } else if (timeLeft <= 30) {
                    bar.setColor(BarColor.YELLOW);
                } else {
                    bar.setColor(BarColor.GREEN);
                }

                timeLeft--;
            }
        }.runTaskTimer(plugin, 0L, 20L);
    }

    // Boss sava≈üƒ± BossBar
    public void bossFightBar(Plugin plugin, Player player, String bossName) {
        BossBar bar = Bukkit.createBossBar(
            ChatColor.DARK_RED + "‚öî " + bossName + " ‚öî",
            BarColor.RED,
            BarStyle.SEGMENTED_20
        );

        bar.addPlayer(player);
        bar.setProgress(1.0);
        bar.setVisible(true);

        // Boss canƒ± azalƒ±rken sim√ºlasyon
        new BukkitRunnable() {
            double health = 1.0;

            @Override
            public void run() {
                health -= 0.05; // Her tick %5 azalt

                if (health <= 0) {
                    bar.setTitle(ChatColor.GREEN + "‚úì " + bossName + " Yenildi!");
                    bar.setColor(BarColor.GREEN);
                    bar.setProgress(0.0);

                    player.playSound(player.getLocation(), Sound.ENTITY_ENDER_DRAGON_DEATH, 1.0f, 1.0f);

                    Bukkit.getScheduler().runTaskLater(plugin, () -> {
                        bar.removePlayer(player);
                    }, 60L);

                    cancel();
                    return;
                }

                bar.setProgress(health);

                // Can seviyesine g√∂re renk
                if (health < 0.3) {
                    bar.setColor(BarColor.RED);
                } else if (health < 0.6) {
                    bar.setColor(BarColor.YELLOW);
                }
            }
        }.runTaskTimer(plugin, 0L, 10L);
    }

    // T√ºm oyunculara BossBar
    public void globalBossBar(String message, BarColor color, int durationSeconds) {
        BossBar bar = Bukkit.createBossBar(message, color, BarStyle.SOLID);

        for (Player player : Bukkit.getOnlinePlayers()) {
            bar.addPlayer(player);
        }

        bar.setVisible(true);

        Bukkit.getScheduler().runTaskLater(plugin, () -> {
            bar.removeAll();
        }, durationSeconds * 20L);
    }
}
