public class ExampleVariables implements CommandExecutor {

    /* =====================================================
       GLOBAL (SUNUCU GENELÄ°) VARIABLE
       ===================================================== */

    // static = sÄ±nÄ±fa ait
    // Sunucuda TEK tane vardÄ±r
    // Bir oyuncu deÄŸiÅŸtirirse herkes etkilenir
    public static boolean gameStarted = false;


    /* =====================================================
       OYUNCUYA Ã–ZEL VARIABLE (BOOLEAN)
       ===================================================== */

    // Her oyuncunun kendi true / false deÄŸeri olur
    // UUID = oyuncunun benzersiz kimliÄŸi
    public static HashMap<UUID, Boolean> readyStatus = new HashMap<>();


    /* =====================================================
       OYUNCUYA Ã–ZEL VARIABLE (SAYISAL)
       ===================================================== */

    // Oyunculara ait puan, para, skor gibi deÄŸerler
    public static HashMap<UUID, Integer> playerScore = new HashMap<>();


    /* =====================================================
       COMMAND KISMI
       ===================================================== */

    @Override
    public boolean onCommand(CommandSender sender, Command cmd, String label, String[] args) {

        // Komutu sadece oyuncular kullansÄ±n
        if (!(sender instanceof Player)) return true;
        Player player = (Player) sender;

        /* ---------- /baslat ---------- */
        if (cmd.getName().equalsIgnoreCase("baslat")) {

            // Global variable true yapÄ±lÄ±yor
            gameStarted = true;
            player.sendMessage("Oyun baÅŸlatÄ±ldÄ±");

            return true;
        }

        /* ---------- /hazir ---------- */
        if (cmd.getName().equalsIgnoreCase("hazir")) {

            // Oyuncunun hazÄ±r durumu true yapÄ±lÄ±yor
            readyStatus.put(player.getUniqueId(), true);
            player.sendMessage("HazÄ±r olarak iÅŸaretlendin");

            return true;
        }

        /* ---------- /puan ---------- */
        if (cmd.getName().equalsIgnoreCase("puan")) {

            // EÄŸer oyuncunun puanÄ± yoksa 0 kabul edilir
            int eskiPuan = playerScore.getOrDefault(player.getUniqueId(), 0);

            // 10 puan eklenir
            playerScore.put(player.getUniqueId(), eskiPuan + 10);

            player.sendMessage("Yeni puanÄ±n: " + (eskiPuan + 10));
            return true;
        }

        /* ---------- /durum ---------- */
        if (cmd.getName().equalsIgnoreCase("durum")) {

            // Global gameStarted kontrolÃ¼
            if (gameStarted) {
                player.sendMessage("Oyun ÅŸu an AÃ‡IK");
            } else {
                player.sendMessage("Oyun ÅŸu an KAPALI");
            }

            // Oyuncuya Ã¶zel ready kontrolÃ¼
            boolean hazirMi = readyStatus.getOrDefault(player.getUniqueId(), false);

            if (hazirMi) {
                player.sendMessage("HazÄ±r durumdasÄ±n");
            } else {
                player.sendMessage("HazÄ±r deÄŸilsin");
            }

            return true;
        }

        /* ---------- /esyaver ---------- */
        if (cmd.getName().equalsIgnoreCase("esyaver")) {

            // Oyuncuya item verme Ã¶rneÄŸi
            ItemStack item = new ItemStack(Material.APPLE, 32);
            player.getInventory().addItem(item);

            player.sendMessage("32 elma verildi");
            return true;
        }

        return true;
    }
}

